    <script>
        // Sample JSON data (would normally come from an API or database)
        const proposalData = {
            "executive_summary": "Client XYZ is an early-stage beauty brand struggling with brand identity and customer engagement. Kingly Kreationz proposes a custom branding suite, automation funnel, and digital strategy to elevate their presence and streamline lead capture.",
            "client_challenges": [
                {
                    "title": "Inconsistent Brand Presence",
                    "description": "The client lacks a unified logo, color palette, and voice across platforms, making it difficult to stand out."
                },
                {
                    "title": "Manual Lead Collection",
                    "description": "Client uses spreadsheets and DMs to manage inquiries, causing lost leads and disorganized data."
                }
            ],
            "proposed_solutions": [
                {
                    "challenge": "Inconsistent Brand Presence",
                    "solution": {
                        "title": "Complete Visual Identity Package",
                        "description": "Includes logo design, brand color system, typography kit, and brand guidebook.",
                        "value": "Establishes a recognizable, professional presence across all platforms."
                    }
                },
                {
                    "challenge": "Manual Lead Collection",
                    "solution": {
                        "title": "Automated Booking & CRM Setup",
                        "description": "Implement a branded Typeform funnel connected to Notion + Email automations via n8n.",
                        "value": "Ensures every lead is captured, categorized, and followed up automaticallysaving time and increasing conversion."
                    }
                }
            ],
            "implementation_timeline": [
                { "week": "Week 1", "tasks": ["Brand Discovery", "Visual Research", "Form Wireframes"] },
                { "week": "Week 2", "tasks": ["Logo Delivery", "Typeform Setup", "Notion DB Integration"] },
                { "week": "Week 3", "tasks": ["Automation Testing", "Final Revisions", "Client Training"] }
            ],
            "cost_estimates": {
                "branding_package": "$800",
                "automation_setup": "$450",
                "full_bundle_discounted": "$1,100 (saves $150)"
            },
            "next_steps": [
                "Book a final review call using the link provided.",
                "Review and sign the attached service agreement.",
                "Submit 50% deposit to initiate project."
            ],
            "contact_information": {
                "agency": "Kingly Kreationz",
                "email": "support@kinglykreationz.com",
                "phone": "+1 (555) 123-4567",
                "signature": "Let's Build Your Empire.  The Kingly Kreationz Team "
            }
        };

        // Populate the document with data
        document.addEventListener('DOMContentLoaded', function() {
            // Set basic info
            document.getElementById('executive-summary').textContent = proposalData.executive_summary;
            document.getElementById('agency-name').textContent = proposalData.contact_information.agency;
            document.getElementById('contact-email').textContent = proposalData.contact_information.email;
            document.getElementById('contact-phone').textContent = proposalData.contact_information.phone;
            document.getElementById('signature').textContent = proposalData.contact_information.signature;

            // Populate challenges
            const challengesContainer = document.getElementById('challenges-container');
            proposalData.client_challenges.forEach(challenge => {
                const challengeElement = document.createElement('div');
                challengeElement.className = 'challenge-card'; // Use new class
                challengeElement.innerHTML = `
                    <h4 class="challenge-card-title">${challenge.title}</h4>
                    <p class="challenge-card-text">${challenge.description}</p>
                `;
                challengesContainer.appendChild(challengeElement);
            });

            // Populate solutions
            const solutionsContainer = document.getElementById('solutions-container');
            proposalData.proposed_solutions.forEach(item => { // Renamed 'solution' to 'item' to avoid conflict
                const solutionElement = document.createElement('div');
                // Combined solution-card (original custom) with solution-card-item (Tailwind converted)
                solutionElement.className = 'solution-card solution-card-item'; 
                solutionElement.innerHTML = `
                    <div class="flex-items-start">
                        <div class="solution-icon-bg">
                            <i class="fas ${item.solution.title.includes('Visual') ? 'fa-palette' : 'fa-robot'} icon-purple"></i>
                        </div>
                        <div>
                            <h4 class="solution-title">${item.solution.title}</h4>
                            <p class="solution-description">${item.solution.description}</p>
                            <div class="solution-value-badge">
                                <span class="solution-value-text">Value: ${item.solution.value}</span>
                            </div>
                        </div>
                    </div>
                `;
                solutionsContainer.appendChild(solutionElement);
            });

            // Populate timeline
            const timelineContainer = document.getElementById('timeline-container');
            proposalData.implementation_timeline.forEach((timeline, index) => {
                const timelineElement = document.createElement('div');
                timelineElement.className = 'timeline-item'; // Original custom class

                if (index < proposalData.implementation_timeline.length - 1) {
                    timelineElement.innerHTML = '<div class="timeline-connector"></div>'; // Original custom class
                }

                const tasksList = timeline.tasks.map(task => `<li>${task}</li>`).join('');

                timelineElement.innerHTML += `
                    <h4 class="timeline-item-title">${timeline.week}</h4>
                    <ul class="timeline-item-tasks">
                        ${tasksList}
                    </ul>
                `;

                timelineContainer.appendChild(timelineElement);
            });

            // Populate pricing
            const pricingContainer = document.getElementById('pricing-container');
            pricingContainer.innerHTML = `
                <div class="pricing-item">
                    <span class="pricing-label">Branding Package</span>
                    <span class="pricing-value">${proposalData.cost_estimates.branding_package}</span>
                </div>
                <div class="pricing-item" style="margin-bottom: 0;"> <!-- Remove mb for last item -->
                    <span class="pricing-label">Automation Setup</span>
                    <span class="pricing-value">${proposalData.cost_estimates.automation_setup}</span>
                </div>
            `;

            // Populate discount
            const discountContainer = document.getElementById('discount-container');
            const [price, note] = proposalData.cost_estimates.full_bundle_discounted.split(' (');
            discountContainer.innerHTML = `
                <div class="pricing-item mb-2"> <!-- Reused pricing-item for structure -->
                    <span class="discount-title">Full Bundle Discounted</span>
                    <span class="discount-price">${price}</span>
                </div>
                <p class="discount-note">(${note}</p>
            `;

            // Populate next steps
            const nextStepsContainer = document.getElementById('next-steps-container');
            proposalData.next_steps.forEach(step => {
                const stepElement = document.createElement('div');
                stepElement.className = 'flex-items-start';
                stepElement.innerHTML = `
                    <div class="next-step-item-icon-bg">
                        <i class="fas fa-check next-step-icon"></i>
                    </div>
                    <p class="next-step-text">${step}</p>
                `;
                nextStepsContainer.appendChild(stepElement);
            });

            // Add toggle functionality for sections (TARGETING STRATEGY ADJUSTED)
            // We target specific container divs for sections to make it more robust
            const toggleableSections = [
                challengesContainer.parentElement, // div class="content-padding-x content-padding-bottom"
                solutionsContainer.parentElement,  // div class="content-padding-x content-padding-y"
                timelineContainer.parentElement.parentElement, // div class="grid-cols-2-md-gap-8" -> its children timeline/pricing
                nextStepsContainer.parentElement // div class="content-padding-x content-padding-y"
            ];

            toggleableSections.forEach(sectionParent => {
                const heading = sectionParent.querySelector('h3.section-heading');
                // Find the main content div directly after the heading or within the section structure
                let content;
                if (sectionParent.id === 'challenges-container' || sectionParent.id === 'solutions-container' || sectionParent.id === 'next-steps-container') {
                     content = sectionParent; // The container itself is the content for these
                } else if (sectionParent.contains(timelineContainer)) { // Special handling for timeline/pricing parent
                    content = sectionParent.querySelector('.grid-cols-2-md-gap-8 > div:first-child div#timeline-container').parentElement; // Timeline section
                    const pricingContent = sectionParent.querySelector('.grid-cols-2-md-gap-8 > div:last-child > div:first-of-type'); // Pricing section
                    if (heading && content) { // Timeline section toggle
                         heading.classList.add('cursor-pointer', 'toggle-btn');
                         content.classList.add('toggle-content');
                         heading.addEventListener('click', () => {
                            heading.classList.toggle('active');
                            content.classList.toggle('active');
                         });
                    }
                    // Also toggle pricing section with its own heading
                    const pricingHeading = sectionParent.querySelector('.grid-cols-2-md-gap-8 > div:last-child > h3.section-heading');
                    if(pricingHeading && pricingContent) {
                        pricingHeading.classList.add('cursor-pointer', 'toggle-btn');
                        pricingContent.parentElement.classList.add('toggle-content'); // Toggle the parent div of pricing boxes
                         pricingHeading.addEventListener('click', () => {
                            pricingHeading.classList.toggle('active');
                            pricingContent.parentElement.classList.toggle('active');
                         });
                    }
                    return; // Skip generic toggle for this complex parent
                } else {
                    // General case: look for a direct child div that is not the heading itself
                    content = Array.from(sectionParent.children).find(child => child.tagName === 'DIV' && !child.contains(heading));
                }

                if (heading && content) {
                    heading.classList.add('cursor-pointer', 'toggle-btn');
                    content.classList.add('toggle-content');

                    heading.addEventListener('click', () => {
                        heading.classList.toggle('active');
                        content.classList.toggle('active');
                    });
                }
            });
        });

